/*
 *
 */
ConsoleService.prototype.logError = function () {
    this.notifications.unshift({
        type: "error",
        timestamp: new Date().getTime(),
        content: this.concatenateArray(arguments)
    });

    if (this.notifications.length > this.maximumNotificationsCount)
    {
        this.notifications.pop();
    }

    console.error.apply(console, arguments);
};

/*
 *
 */
ConsoleService.prototype.logWarning = function (content) {
    this.notifications.unshift({
        type: "warning",
        timestamp: new Date().getTime(),
        content: this.concatenateArray(arguments)
    });

    if (this.notifications.length > this.maximumNotificationsCount)
    {
        this.notifications.pop();
    }

    console.log.apply(console, arguments);
};

